                </InputRow>
            </FormRow>
        </template>
        <template #footer>
            <Button :color="'secondary'" @click="closeModal">
                Cancel
            </Button>
            <Button :class="{ 'opacity-25': form.processing }" :disabled="form.processing" @click="addComponent">
                Submit
            </Button>
        </template>
    </Modal>
</template>
